import React, { useState } from 'react';
import * as Utils from './Utils';

const FindArtist = () => {
    const [popularity, setPopularity] = useState('');
    const [genre, setGenre] = useState('');
    const [searchResult, setSearchResult] = useState([]);
    const [error, setError] = useState(null);

    const handleSearch = async () => {
        try {
            if (!popularity || !genre) {
                throw new Error('Please select both popularity and genre.');
            }
            const recommendedArtists = await Utils.getRecommendedArtists(popularity, genre);
            setSearchResult(recommendedArtists);
        } catch (error) {
            setError(error.message);
        }
    };

    return (
        <div>
            <h2>Search for Artists</h2>
            <div>
                <label>
                    Popularity:
                    <select value={popularity} onChange={(e) => setPopularity(e.target.value)}>
                        <option value="">Select Popularity</option>
                        <option value="very-popular">Very Popular</option>
                        <option value="popular">Popular</option>
                        <option value="moderate">Moderate</option>
                        <option value="niche">Niche</option>
                        <option value="underground">Underground</option>
                    </select>
                </label>
            </div>
            <div>
                <label>
                    Genre:
                    <input type="text" value={genre} onChange={(e) => setGenre(e.target.value)} />
                </label>
            </div>
            <button onClick={handleSearch}>Search</button>
            {error && <p>Error: {error}</p>}
            {searchResult.length > 0 && (
                <div>
                    <h3>Recommended Artists</h3>
                    <ul>
                        {searchResult.map(artist => (
                            <li key={artist.id}>{artist.name}</li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default FindArtist;
