// src/TierList.js
import React, { useState } from 'react';
import Tier from './Tier';
import '../styles/TierList.css';

const TierList = () => {
  const tiers = ['S', 'A', 'B', 'C', 'D'];
  const [droppedItems, setDroppedItems] = useState({
    S: [],
    A: [],
    B: [],
    C: [],
    D: [],
  });

  const handleDrop = (itemId, tier) => {
    const updatedItems = { ...droppedItems, [tier]: [...droppedItems[tier], itemId] };
    setDroppedItems(updatedItems);
  };

  const handleClearDrop = (tier, itemId) => {
    const updatedItems = {
      ...droppedItems,
      [tier]: droppedItems[tier].filter((id) => id !== itemId),
    };
    setDroppedItems(updatedItems);
  };

  return (
    <div className="tier-list-container">
      <h1>Tier List</h1>
      <div className="tier-list">
        {tiers.map((tier) => (
          <div key={tier} className="tier-container">
            <Tier tier={tier} onDrop={handleDrop} />
          </div>
        ))}
      </div>

      <div className="tier-tables">
        {tiers.map((tier) => (
          <div key={tier} className="tier-table">
            <h2>{tier}</h2>
            <ul className="dropped-items">
              {droppedItems[tier].map((itemId) => (
                <li key={itemId}>
                  {itemId}
                  <button onClick={() => handleClearDrop(tier, itemId)}>Remove</button>
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TierList;
