import React, { useState } from 'react';
import '../styles/TierList.css';

const TierList = () => {
  const tiers = ['S', 'A', 'B', 'C', 'D'];
  const items = ['Item 1', 'Item 2', 'Item 3', 'TEST'];

  const [droppedItems, setDroppedItems] = useState({});

  const handleDrop = (tier, itemId, e) => {
    e.preventDefault(); // Prevent default behavior of drag-and-drop
    setDroppedItems((prevItems) => ({
      ...prevItems,
      [tier]: [...(prevItems[tier] || []), itemId],
    }));
  };

  const handleClearDrop = (tier, itemId) => {
    const updatedItems = { ...droppedItems };
    updatedItems[tier] = updatedItems[tier].filter((id) => id !== itemId);
    setDroppedItems(updatedItems);
  };

  return (
    <div className="tier-list-container">
      <h1>Tier List</h1>

      <div className="tier-list">
        {tiers.map((tier) => (
          <div key={tier} className="tier-pair">
            <div className={`tier-name tier-${tier.toLowerCase()}`}>
              <h2>{tier}</h2>
              <div className="dropped-items">
                {droppedItems[tier]?.map((itemId) => (
                  <div key={itemId} className="dropped-item">
                    {itemId}
                    <div className="remove-icon" onClick={() => handleClearDrop(tier, itemId)}>
                      X
                    </div>
                  </div>
                ))}
              </div>
            </div>
            <Tier tier={tier} onDrop={(itemId, e) => handleDrop(tier, itemId, e)} />
          </div>
        ))}
      </div>

      <div className="items-container">
        {items.map((item) => (
          <div
            key={item}
            className="item"
            draggable
            onDragStart={(e) => e.dataTransfer.setData('text/plain', item)}
          >
            {item}
          </div>
        ))}
      </div>
    </div>
  );
};

export default TierList;
