import React, { useState, useEffect } from 'react';
import { Routes, Route } from 'react-router-dom';
import Header from './components/Header';
import Home from './components/Home';
import Profile from './components/Profile';
import FindMusic from './components/FindMusic';
import 'bootstrap/dist/css/bootstrap.min.css';
import "./styles/styles.css";
import './App.css';

function App() {
  const [theme, setTheme] = useState(() => localStorage.getItem('theme') || 'main'); // Initialize theme from localStorage or default to 'main'
  const [sessionExpired, setSessionExpired] = useState(false); // State for session expiration popup

  useEffect(() => {
    // Check for session expiration on component mount
    const isLoggedIn = localStorage.getItem('access_token') !== null;
    const loginTime = localStorage.getItem('login_time');

    if (isLoggedIn && loginTime) {
      const currentTime = Date.now();
      const sessionDuration = 60 * 60 * 1000; // Session duration in milliseconds (1 hour)

      if (currentTime - parseInt(loginTime) > sessionDuration) {
        // Session expired, set sessionExpired state to true
        setSessionExpired(true);
      }
    }
  }, []);

  useEffect(() => {
    // Display session expiration alert if sessionExpired is true
    if (sessionExpired) {
      alert('Sesja wygasła. (401)'); // Display popup for session expiration
    }
  }, [sessionExpired]);

  return (
    <div className="App">
      <Header setTheme={setTheme} /> {/* Pass setTheme as a prop */}
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="FindMusic" element={<FindMusic />} />
        <Route path="profile" element={<Profile />} />
      </Routes>
    </div>
  );
}

export default App;
