import React, { useState, useEffect } from 'react';
import { openWeatherApiKey, openWeatherApiUrl, spotifyClientId, spotifyClientSecret, spotifyRedirectUri } from './ApiKeys';

// Constants
const apiKey = openWeatherApiKey;
const clientId = spotifyClientId;
const clientSecret = spotifyClientSecret;
const redirectUri = spotifyRedirectUri;
const apiUrl = openWeatherApiUrl;

// Authentication flow
async function authenticate() {
    const scopes = ['user-read-private', 'user-read-email'];

    window.location.href = `https://accounts.spotify.com/authorize?client_id=${clientId}&redirect_uri=${redirectUri}&scope=${scopes.join('%20')}&response_type=code`;
}
// Function to fetch user's top tracks
async function fetchTopTracks(accessToken) {
    try {
      const response = await fetch('https://api.spotify.com/v1/me/top/tracks', {
        headers: {
          Authorization: `Bearer ${accessToken}`
        }
      });

      if (!response.ok) {
        throw new Error(`Failed to fetch top tracks. Status: ${response.status}`);
      }

      const data = await response.json();
      return data.items.map(track => track.id);
    } catch (error) {
      console.error('Error fetching top tracks:', error);
      throw error;
    }
  }

  // Function to fetch recommendations based on top tracks
  async function getRecommendations(token) {
    try {
      const topTracksIds = await fetchTopTracks(token);

      // Endpoint reference: https://developer.spotify.com/documentation/web-api/reference/get-recommendations
      const res = await fetch(`https://api.spotify.com/v1/recommendations?limit=5&seed_tracks=${topTracksIds.join(',')}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        }
      });

      const data = await res.json();
      return data.tracks;
    } catch (error) {
      console.error('Error getting recommendations:', error);
      throw error;
    }
  }

  // Modify the useEffect hook to fetch recommendations
  useEffect(() => {
    const accessToken = localStorage.getItem('access_token');
    if (accessToken) {
      getRecommendations(accessToken)
        .then(recommendedTracks => {
          // Handle recommended tracks
          console.log('Recommended Tracks:', recommendedTracks);
        })
        .catch(error => {
          console.error('Error fetching recommendations:', error);
        });
    } else {
      console.error('Access token not found');
    }
  }, []);


// Authorization code handler
async function handleAuthorizationCode() {
    const params = new URLSearchParams(window.location.search);
    const authorizationCode = params.get('code');

    if (authorizationCode) {
        try {
            const response = await fetch('https://accounts.spotify.com/api/token', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded',
                    Authorization: `Basic ${btoa(`${clientId}:${clientSecret}`)}`
                },
                body: `grant_type=authorization_code&code=${authorizationCode}&redirect_uri=${redirectUri}`
            });

            if (!response.ok) {
                throw new Error(`Failed to exchange authorization code for access token. Status: ${response.status}`);
            }

            const data = await response.json();
            const accessToken = data.access_token;

            if (!accessToken) {
                throw new Error('Access token not found in response');
            }

            localStorage.setItem('access_token', accessToken);
            window.location.href = '/';
        } catch (error) {
            console.error('Error exchanging authorization code:', error);
        }
    }
}

// Fetch user profile
async function getProfile(accessToken, refreshToken) {
    try {
        const response = await fetch('https://api.spotify.com/v1/me', {
            headers: {
                Authorization: 'Bearer ' + accessToken
            }
        });

        const data = await response.json();
        return data;
    } catch (error) {
        if (error.message === "Unauthorized") {
            try {
                const newAccessToken = await refreshAccessToken(refreshToken);
                localStorage.setItem('access_token', newAccessToken);
                return getProfile(newAccessToken, refreshToken);
            } catch (refreshError) {
                console.error('Error refreshing access token:', refreshError);
                throw refreshError;
            }
        } else {
            throw error;
        }
    }
}

// Refresh access token
async function refreshAccessToken(refreshToken) {
    const clientCredentials = btoa(`${spotifyClientId}:${spotifyClientSecret}`);
    const spotifyTokenResponse = await fetch("https://accounts.spotify.com/api/token", {
        method: "POST",
        headers: {
            "Content-Type": "application/x-www-form-urlencoded",
            Authorization: `Basic ${clientCredentials}`,
        },
        body: `grant_type=refresh_token&refresh_token=${refreshToken}`,
        cache: "no-cache"
    });

    const spotifyTokenData = await spotifyTokenResponse.json();
    return spotifyTokenData.access_token;
}

function Home() {
    const [city, setCity] = useState('');
    const [weatherData, setWeatherData] = useState(null);
    const [errorMessage, setErrorMessage] = useState('');
    const [userName, setUserName] = useState('');
    const [loggedIn, setLoggedIn] = useState(false);
    const [theme, setTheme] = useState(() => localStorage.getItem('theme') || 'main');

    const handleLogin = () => {
        window.location.href = `https://accounts.spotify.com/authorize?response_type=code&client_id=${clientId}&redirect_uri=${redirectUri}&scope=user-read-private%20user-read-email`;
    };

    const handleLogout = () => {
        localStorage.removeItem('access_token');
        localStorage.removeItem('refresh_token');
        setLoggedIn(false);
        window.location.href = '/';
    };

    useEffect(() => {
        handleAuthorizationCode();
    }, []);

    useEffect(() => {
        const lastCity = localStorage.getItem('lastCity');
        if (lastCity) {
            setCity(lastCity);
            fetchWeatherData(lastCity);
        }

        console.log('Access Token from Local Storage:', localStorage.getItem('access_token'));

        const accessToken = localStorage.getItem('access_token');
        if (accessToken) {
            console.log('Access Token:', accessToken);

            getProfile(accessToken)
                .then(data => {
                    if (data) {
                        console.log('User Profile:', data);
                        setUserName(data.display_name);
                    }
                })
                .catch(error => {
                    console.error('Error fetching user profile:', error);
                });
        } else {
            console.error('Access token not found');
        }
    }, []);

    useEffect(() => {
        const accessToken = localStorage.getItem('access_token');
        const refreshToken = localStorage.getItem('refresh_token');

        if (accessToken) {
            getProfile(accessToken, refreshToken)
                .then(data => {
                    if (data) {
                        setUserName(data.display_name);
                        setLoggedIn(true);
                    }
                })
                .catch(error => {
                    console.error('Error fetching user profile:', error);
                });
        } else {
            console.error('Access token not found');
        }
    }, []);

    useEffect(() => {
        document.body.classList.toggle('light-theme', theme === 'light');
        localStorage.setItem('theme', theme);
    }, [theme]);

    const fetchWeatherData = async (city) => {
        try {
            const response = await fetch(apiUrl + city + `&appid=${apiKey}`);
            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }
            const data = await response.json();
            setWeatherData(data);
            setErrorMessage('');
            localStorage.setItem('lastCity', city);
        } catch (error) {
            console.error(error);
            setWeatherData(null);
            setErrorMessage('Error fetching weather data. Please try again later.');
        }
    };

    const handleSearch = () => {
        fetchWeatherData(city);
    };

    const handleChange = (e) => {
        setCity(e.target.value);
    };

    const handleKeyDown = (e) => {
        if (e.key === 'Enter') {
            handleSearch();
        }
    };

    const toggleTheme = () => {
        setTheme((prevTheme) => (prevTheme === 'main' ? 'light' : 'main'));
    };

    return (
        <>
            <div className="container">
                <div className="card">
                    <div className="search">
                        <input
                            type="text"
                            placeholder="Enter city name"
                            spellCheck="false"
                            value={city}
                            onChange={handleChange}
                            onKeyDown={handleKeyDown}
                        />
                        <button onClick={handleSearch}>Search</button>
                    </div>
                    {errorMessage && <div className="error"><p>{errorMessage}</p></div>}
                    {weatherData && (
                        <div className="weather">
                            <h3>Hello, {userName}!</h3>
                            <h2>Weather in {weatherData.name}</h2>
                            <p>Temperature: {Math.round(weatherData.main.temp)}Â°C</p>
                            <p>Humidity: {weatherData.main.humidity}%</p>
                            <p>Wind Speed: {weatherData.wind.speed} km/h</p>
                        </div>
                    )}
                </div>
                {/* Display top tracks */}
          {topTracks.length > 0 && (
            <div>
              <h2>Top Tracks</h2>
              <ul>
                {topTracks.map(track => (
                  <li key={track.id}>{track.name} by {track.artists.map(artist => artist.name).join(', ')}</li>
                ))}
              </ul>
            </div>
          )}
                <button className="theme-btn" onClick={toggleTheme}>Theme</button>
                {loggedIn ? (
                    <button onClick={handleLogout}>Logout</button>
                ) : (
                    <button onClick={handleLogin}>Login with Spotify</button>
                )}
            </div>

        </>
    );
}

export default Home;
