import React from 'react';
import '../styles/TierList.css';

const Tier = ({ tier, onDrop }) => {
  const handleDrop = (e) => {
    e.preventDefault();
    const itemId = e.dataTransfer.getData('text/plain');
    onDrop(tier, itemId); // Pass both the tier and itemId to the onDrop function
  };

  const allowDrop = (e) => {
    e.preventDefault();
  };

  return (
    <div className="tier" onDrop={handleDrop} onDragOver={allowDrop}>
      <h2>{tier}</h2>
      <div className="drop-zone">Drop Item Here</div> {/* Display drop zone text */}
      {droppedItems[tier]?.map((itemId) => (
                  <li key={itemId}>
                    {itemId}
                    <button onClick={() => handleClearDrop(tier, itemId)}>Remove</button>
                  </li>
                ))}
    </div>
  );
};

export default Tier;
