import React, { useState, useEffect } from 'react';
import { Routes, Route, useHistory } from 'react-router-dom';
import Header from './components/Header';
import Home from './components/Home';
import Profile from './components/Profile';
import FindMusic from './components/FindMusic';
import TierListPage from './components/TierListPage';
import 'bootstrap/dist/css/bootstrap.min.css';
import './styles/styles.css';
import './App.css';
import * as Utils from './Utils'; // Import your utility functions

function App() {
  const history = useHistory();
  const [currentPreview, setCurrentPreview] = useState(null);
  const [playingTrack, setPlayingTrack] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);

  useEffect(() => {
    return history.listen(() => {
      if (currentPreview) {
        Utils.pausePreview(currentPreview); // Pause the audio preview
        setCurrentPreview(null); // Reset current audio preview
        setPlayingTrack(null); // Reset playing track
        setIsPlaying(false); // Reset playing state
      }
    });
  }, [currentPreview, history]);

  return (
    <div className="App">
      <Header />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="FindMusic" element={<FindMusic />} />
        <Route path="TierListPage" element={<TierListPage />} />
        <Route path="profile" element={<Profile />} />
      </Routes>
    </div>
  );
}

export default App;
