import React, { useState, useEffect } from 'react';
import { Routes, Route } from 'react-router-dom';
import Header from './components/Header';
import Home from './components/Home';
import Profile from './components/Profile';
import FindMusic from './components/FindMusic';
import TierListPage from './components/TierListPage';
// import Feelings from './components/Feelings';
import 'bootstrap/dist/css/bootstrap.min.css';
import './styles/styles.css';
import './App.css';

function App() {
  const [theme, setTheme] = useState(() => localStorage.getItem('theme') || 'main'); // Initialize theme from localStorage or default to 'main'
  const [currentPreview, setCurrentPreview] = useState(null); // State to track current audio preview

  const toggleTheme = () => {
    const newTheme = theme === 'main' ? 'light' : 'main';
    setTheme(newTheme);
    localStorage.setItem('theme', newTheme);
    applyTheme(newTheme);
  };

  const applyTheme = (theme) => {
    document.body.classList.toggle('light-theme', theme === 'light');
  };

  useEffect(() => {
    applyTheme(theme);
  }, [theme]);

  useEffect(() => {
    const handleRouteChange = () => {
      console.log('Current Route:', window.location.pathname);
      if (currentPreview) {
        currentPreview.pause();
      }
    };

    // Listen for changes in window.location.pathname directly
    window.onpopstate = handleRouteChange;

    return () => {
      window.onpopstate = null;
    };
  }, [currentPreview]);

  return (
    <div className="App">
      <Header toggleTheme={toggleTheme} /> {/* Pass toggleTheme as a prop */}
      <Routes>
        <Route path="/" element={<Home toggleTheme={toggleTheme} />} />
        <Route path="FindMusic" element={<FindMusic toggleTheme={toggleTheme} />} />
        <Route path="TierListPage" element={<TierListPage toggleTheme={toggleTheme} />} />
        <Route path="profile" element={<Profile toggleTheme={toggleTheme} />} />
      </Routes>
    </div>
  );
}

export default App;
