import React from 'react';
import { Link } from 'react-router-dom';
import { loggedin, handleLogout, pausePreview } from './Utils'; // Import loggedIn, handleLogout, and pausePreview functions

export default function Header({ toggleTheme, currentPreview, setIsPlaying }) {
    const handleHeaderItemClick = () => {
        // Pause the music when a header item is clicked
        pauseMusic();
    };

    const pauseMusic = () => {
        // Pause the music
        if (currentPreview) {
            pausePreview(currentPreview);
            setIsPlaying(false); // Update playing state to false when pausing
        }
    };

    return (
        <>
            <h1 className="is-size-1">Spot The Weather</h1>
            <header>
                <nav className="nawigacja">
                    <button className="theme-btn" onClick={toggleTheme}>Zmień Motyw</button>
                    <ul className="nav justify-content-center">
                        <li className="nav-item" onClick={handleHeaderItemClick}>
                            <Link to="/">Strona Główna</Link>
                        </li>
                        <li className="nav-item" onClick={handleHeaderItemClick}>
                            <Link to="FindMusic">Wyszukiwarka Muzyczna</Link>
                        </li>
                        <li className="nav-item" onClick={handleHeaderItemClick}>
                            <Link to="TierListPage">Tier List</Link>
                        </li>
                        <li className="nav-item" onClick={handleHeaderItemClick}>
                            <Link to="Profile">Profil</Link>
                        </li>
                        <li className="nav-item">
                            {loggedin() ? ( // Check if user is logged in
                                <button className="login-btn" onClick={() => {
                                    handleLogout();
                                    pauseMusic(); // Pause music when logging out
                                }}>Wyloguj</button>
                            ) : (
                                ""
                            )}
                        </li>
                    </ul>
                </nav>
            </header>
        </>
    );
}
