import React, { useState, useEffect } from 'react';
import { getTopArtists, loggedin } from './Utils'; // Import necessary functions from Utils.js

const TierList = () => {
  const tiers = ['S', 'A', 'B', 'C', 'D'];
  const [droppedItems, setDroppedItems] = useState({ S: [], A: [], B: [], C: [], D: [] });

  useEffect(() => {
    const fetchTopArtistsData = async () => {
      try {
        if (!loggedin()) {
          console.log('User not logged in');
          return;
        }

        const accessToken = localStorage.getItem('access_token');

        // Fetch top artists with a larger limit to ensure more unique artists
        const topArtistsData = await getTopArtists(accessToken, 50, 0);
        if (!topArtistsData || !topArtistsData.items || topArtistsData.items.length === 0) {
          console.log('No top artists data');
          return;
        }

        // Filter out duplicate artists and limit to 10 unique artists
        const uniqueArtists = [...new Map(topArtistsData.items.map(item => [item.id, item])).values()].slice(0, 10);

        // Update droppedItems state with fetched artists
        const updatedDroppedItems = {
          S: uniqueArtists.map(artist => ({ ...artist, tier: 'S' })),
          A: [],
          B: [],
          C: [],
          D: []
        };
        setDroppedItems(updatedDroppedItems);
      } catch (error) {
        console.error('Error fetching top artists:', error);
      }
    };

    // Call the function to fetch data when the component mounts
    fetchTopArtistsData();
  }, []); // Empty dependency array ensures this effect runs once on component mount

  return (
    <div className="tier-list-container">
      <h1>Tier List</h1>
      {/* Render Tier components for each tier */}
      {tiers.map((tier) => (
        <div key={tier} className="tier-pair">
          <div className={`tier-name tier-${tier.toLowerCase()}`}>
            <h2>{tier}</h2>
          </div>
          {/* Render dropped items (artists) for the current tier */}
          <div className="tier">
            {droppedItems[tier]?.map((artist) => (
              <div key={artist.id} className="artist-item">
                <img src={artist.images[0]?.url} alt={artist.name} style={{ width: 90, height: 90 }} />
                <p>{artist.name}</p>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default TierList;
