// src/TierList.js
import React, { useState } from 'react';
import Tier from './Tier';
import './TierList.css';

const TierList = () => {
  const tiers = ['S', 'A', 'B', 'C', 'D'];

  const [droppedItem, setDroppedItem] = useState(null);

  const handleDrop = (itemId, tier) => {
    // Allow only one item to be dropped at a time
    setDroppedItem({ itemId, tier });
  };

  const handleClearDrop = () => {
    setDroppedItem(null); // Clear dropped item
  };

  return (
    <div className="tier-list-container">
      <h1>Tier List</h1>
      <div className="tier-list">
        {tiers.map((tier) => (
          <Tier key={tier} tier={tier} onDrop={handleDrop} />
        ))}
      </div>

      <div className="dropped-item-container">
        {droppedItem && (
          <div className="dropped-item">
            <span>{droppedItem.itemId}</span>
            <button onClick={handleClearDrop}>Remove</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default TierList;
