import React, { useState, useEffect } from 'react';
import { getTopArtists, loggedin } from './Utils';

const TierList = () => {
  const tiers = ['S', 'A', 'B', 'C', 'D'];

  const [droppedItems, setDroppedItems] = useState({ S: [], A: [], B: [], C: [], D: [] });

  useEffect(() => {
    const fetchTopArtistsData = async () => {
      try {
        if (!loggedin()) {
          console.log('User not logged in');
          return;
        }

        const accessToken = localStorage.getItem('access_token');
        const topArtistsData = await getTopArtists(accessToken);

        const initialDroppedItems = tiers.reduce((acc, tier) => {
          acc[tier] = [];
          return acc;
        }, {});

        setDroppedItems({ ...initialDroppedItems, items: topArtistsData.items });
      } catch (error) {
        console.error('Error fetching top artists:', error);
      }
    };

    fetchTopArtistsData();
  }, []);

  const handleDrop = (tier, itemId, e) => {
    e.preventDefault();
    setDroppedItems((prevItems) => ({
      ...prevItems,
      [tier]: [...prevItems[tier], itemId],
    }));
  };

  const handleClearDrop = (tier, itemId) => {
    setDroppedItems((prevItems) => ({
      ...prevItems,
      [tier]: prevItems[tier].filter((item) => item.id !== itemId),
    }));
  };

  const allowDrop = (e) => {
    e.preventDefault();
  };

  const DraggableArtist = ({ artist }) => {
    const imageUrl = artist.images?.length > 0 ? artist.images[0].url : '';

    const handleDragStart = (e) => {
      e.dataTransfer.setData('text/plain', artist.id);
    };

    return (
      <div className="draggable-artist" draggable onDragStart={handleDragStart}>
        {imageUrl ? (
          <img src={imageUrl} alt={artist.name} style={{ width: 90, height: 90 }} />
        ) : (
          <div style={{ width: 90, height: 90, backgroundColor: 'lightgray', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
            No Image
          </div>
        )}
        <p>{artist.name}</p>
      </div>
    );
  };

  const Tier = ({ tier }) => {
    const handleDropLocal = (e) => {
      const itemId = e.dataTransfer.getData('text/plain');
      handleDrop(tier, itemId, e);
    };

    const handleItemClick = (itemId) => {
      handleClearDrop(tier, itemId);
    };

    return (
      <div className="tier" onDrop={handleDropLocal} onDragOver={allowDrop}>
        <div className="drop-zone">Drop Item Here</div>
        {droppedItems[tier]?.map((artist) => (
          <div key={artist.id} className="artist-item" onClick={() => handleItemClick(artist.id)}>
            <DraggableArtist artist={artist} />
          </div>
        ))}
      </div>
    );
  };

  return (
    <div className="tier-list-container">
      <h1>Tier List</h1>
      <div className="tier-list">
        {tiers.map((tier) => (
          <div key={tier} className="tier-pair">
            <div className={`tier-name tier-${tier.toLowerCase()}`}>
              <h2>{tier}</h2>
            </div>
            <Tier tier={tier} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default TierList;
