import React, { useState, useEffect } from 'react';
import * as Utils from './Utils';

const FindArtist = ({ onSearch }) => {
    const [popularity, setPopularity] = useState('');
    const [genre, setGenre] = useState('');
    const [searchResult, setSearchResult] = useState([]);
    const [error, setError] = useState(null);
    const [accessToken, setAccessToken] = useState('');
    const [numArtists, setNumArtists] = useState(5); // Default to 5 artists

    useEffect(() => {
        const token = localStorage.getItem('access_token');
        if (token) {
            setAccessToken(token);
        }
    }, []);

    const handleSearch = async () => {
        try {
            if (!accessToken) {
                throw new Error('Access token not found');
            }
            const query = {
                popularity,
                genres: genre,
            };
            const artists = await Utils.getRecommendedArtists(accessToken, genre, popularity, numArtists); // Use numArtists as limit
            setSearchResult(artists || []);
        } catch (error) {
            setError(error.message);
        }
    };

    return (
        <div>
            <h2>Search for Artists</h2>
            <div>
                <label>
                    Popularity:
                    <select value={popularity} onChange={(e) => setPopularity(e.target.value)}>
                        <option value="">Select Popularity</option>
                        <option value="very-popular">Very Popular</option>
                        <option value="popular">Popular</option>
                        <option value="moderate">Moderate</option>
                        <option value="niche">Niche</option>
                        <option value="underground">Underground</option>
                    </select>
                </label>
            </div>
            <div>
                <label>
                    Genre:
                    <input type="text" value={genre} onChange={(e) => setGenre(e.target.value)} />
                </label>
            </div>
            <div>
                <label>
                    Number of Artists:
                    <input type="number" value={numArtists} min={1} max={15} onChange={(e) => setNumArtists(parseInt(e.target.value))} />
                </label>
            </div>
            <button onClick={handleSearch}>Search</button>
            {error && <p>Error: {error}</p>}
            {searchResult && searchResult.length > 0 && (
                <div>
                    <h3>Recommended Artists</h3>
                    <ul>
                        {searchResult.map(artist => (
                            <li key={artist.id}>{artist.name}</li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default FindArtist;
