import React, { useState, useEffect } from 'react';
import { getTopArtists } from './utils'; // Import the function to fetch top artists

const TierList = () => {
  const tiers = ['S', 'A', 'B', 'C', 'D'];

  // Initialize droppedItems state with empty arrays for each tier
  const initialDroppedItems = tiers.reduce((acc, tier) => {
    acc[tier] = [];
    return acc;
  }, {});

  const [droppedItems, setDroppedItems] = useState({ ...initialDroppedItems, items: [] });

  useEffect(() => {
    const fetchArtists = async () => {
      try {
        // Fetch top artists from API
        const topArtistsData = await getTopArtists();

        // Store fetched artists as items
        const items = topArtistsData.items.map((artist) => ({
          id: artist.id,
          name: artist.name,
        }));

        // Set droppedItems state with fetched artists
        setDroppedItems((prevItems) => ({ ...prevItems, items }));
      } catch (error) {
        console.error('Error fetching top artists:', error);
      }
    };

    // Call fetchArtists when component mounts
    fetchArtists();
  }, []); // Empty dependency array ensures this effect runs once on component mount

  const handleDrop = (tier, itemId, e) => {
    e.preventDefault();

    // Add the dropped item (artist) to the specified tier
    setDroppedItems((prevItems) => ({
      ...prevItems,
      [tier]: [...prevItems[tier], itemId],
    }));
  };

  const handleClearDrop = (tier, itemId) => {
    // Remove the dropped item (artist) from the specified tier
    setDroppedItems((prevItems) => ({
      ...prevItems,
      [tier]: prevItems[tier].filter((item) => item.id !== itemId),
    }));
  };

  const allowDrop = (e) => {
    e.preventDefault();
  };

  // Define Tier component within TierList component
  const Tier = ({ tier }) => {
    const handleDropLocal = (itemId, e) => {
      handleDrop(tier, itemId, e); // Call the handleDrop function from TierList component
    };

    const handleItemClick = (itemId) => {
      // Remove item (artist) from droppedItems of the current tier
      handleClearDrop(tier, itemId);
    };

    return (
      <div className="tier" onDrop={(e) => handleDropLocal(e.dataTransfer.getData('text/plain'), e)} onDragOver={allowDrop}>
        <div className="drop-zone">Drop Item Here</div>
        {/* Render dropped items (artists) for the current tier */}
        {droppedItems[tier]?.map((item) => (
          <div key={item.id} onClick={() => handleItemClick(item.id)}>
            {item.name}
          </div>
        ))}
      </div>
    );
  };

  return (
    <div className="tier-list-container">
      <h1>Tier List</h1>

      <div className="tier-list">
        {/* Render Tier components for each tier */}
        {tiers.map((tier) => (
          <div key={tier} className="tier-pair">
            <div className={`tier-name tier-${tier.toLowerCase()}`}>
              <h2>{tier}</h2>
            </div>
            {/* Render Tier component for the current tier */}
            <Tier tier={tier} /> {/* Pass tier prop to Tier component */}
          </div>
        ))}
      </div>

      {/* Render draggable items (artists) */}
      <div className="items-container">
        {droppedItems.items?.map((item) => (
          <div
            key={item.id}
            className="item"
            draggable
            onDragStart={(e) => e.dataTransfer.setData('text/plain', item.id)}
          >
            {item.name}
          </div>
        ))}
      </div>
    </div>
  );
};

export default TierList;
