import React, { useState, useEffect } from 'react';
import { loggedin } from './Utils'; // Import the necessary functions from utils.js

const TierList = () => {
  const tiers = ['S', 'A', 'B', 'C', 'D'];

  const [droppedItems, setDroppedItems] = useState({ S: [], A: [], B: [], C: [], D: [] });

  useEffect(() => {
    const fetchRandomArtists = async () => {
      try {
        if (!loggedin()) {
          console.log('User not logged in');
          return;
        }

        // Simulated random artists data
        const randomArtists = await getRandomArtists(10); // Fetch 10 random artists
        const initialDroppedItems = tiers.reduce((acc, tier) => {
          acc[tier] = [];
          return acc;
        }, {});

        setDroppedItems({ ...initialDroppedItems, items: randomArtists });
      } catch (error) {
        console.error('Error fetching random artists:', error);
      }
    };

    fetchRandomArtists();
  }, []);

  // Simulated function to fetch random artists (replace with actual implementation)
  const getRandomArtists = async (count) => {
    try {
      // Simulated random artists data
      const allArtists = [
        { id: '1', name: 'Artist 1', images: [{ url: 'url1' }] },
        { id: '2', name: 'Artist 2', images: [] },
        { id: '3', name: 'Artist 3', images: [{ url: 'url3' }] },
        { id: '4', name: 'Artist 4', images: [{ url: 'url4' }] },
        { id: '5', name: 'Artist 5', images: [] },
        { id: '6', name: 'Artist 6', images: [{ url: 'url6' }] },
        { id: '7', name: 'Artist 7', images: [] },
        { id: '8', name: 'Artist 8', images: [{ url: 'url8' }] },
        { id: '9', name: 'Artist 9', images: [{ url: 'url9' }] },
        { id: '10', name: 'Artist 10', images: [] },
      ];

      // Shuffle the array of artists to get a random selection
      const shuffledArtists = shuffleArray(allArtists);

      // Return a subset of the shuffled array based on the specified count
      return shuffledArtists.slice(0, count);
    } catch (error) {
      console.error('Error fetching random artists:', error);
      return [];
    }
  };

  // Utility function to shuffle an array (Fisher-Yates shuffle)
  const shuffleArray = (array) => {
    const shuffled = [...array];
    for (let i = shuffled.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
    }
    return shuffled;
  };

  const handleDrop = (tier, itemId, e) => {
    e.preventDefault();
    setDroppedItems((prevItems) => ({
      ...prevItems,
      [tier]: [...prevItems[tier], itemId],
    }));
  };

  const handleClearDrop = (tier, itemId) => {
    setDroppedItems((prevItems) => ({
      ...prevItems,
      [tier]: prevItems[tier].filter((item) => item.id !== itemId),
    }));
  };

  const allowDrop = (e) => {
    e.preventDefault();
  };

  const DraggableArtist = ({ artist }) => {
    const imageUrl = artist.images.length > 0 ? artist.images[0].url : '';

    const handleDragStart = (e) => {
      e.dataTransfer.setData('text/plain', artist.id);
    };

    return (
      <div className="draggable-artist" draggable onDragStart={handleDragStart}>
        {imageUrl ? (
          <img src={imageUrl} alt={artist.name} style={{ width: 90, height: 90 }} />
        ) : (
          <div
            style={{
              width: 90,
              height: 90,
              backgroundColor: 'lightgray',
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
            }}
          >
            No Image
          </div>
        )}
        <p>{artist.name}</p>
      </div>
    );
  };

  const Tier = ({ tier }) => {
    const handleDropLocal = (e) => {
      const itemId = e.dataTransfer.getData('text/plain');
      handleDrop(tier, itemId, e);
    };

    const handleItemClick = (itemId) => {
      handleClearDrop(tier, itemId);
    };

    return (
      <div className="tier" onDrop={handleDropLocal} onDragOver={allowDrop}>
        <div className="drop-zone">Drop Item Here</div>
        {droppedItems[tier]?.map((artist) => (
          <div key={artist.id} className="artist-item" onClick={() => handleItemClick(artist.id)}>
            {artist.images.length > 0 ? (
              <img src={artist.images[0].url} alt={artist.name} style={{ width: 90, height: 90 }} />
            ) : (
              <div
                style={{
                  width: 90,
                  height: 90,
                  backgroundColor: 'lightgray',
                  display: 'flex',
                  justifyContent: 'center',
                  alignItems: 'center',
                }}
              >
                No Image
              </div>
            )}
            <p>{artist.name}</p>
          </div>
        ))}
      </div>
    );
  };

  return (
    <div className="tier-list-container">
      <h1>Tier List</h1>
      <div className="tier-list">
        {tiers.map((tier) => (
          <div key={tier} className="tier-pair">
            <div className={`tier-name tier-${tier.toLowerCase()}`}>
              <h2>{tier}</h2>
            </div>
            <Tier tier={tier} />
          </div>
        ))}
      </div>
      <div className="items-container">
        {droppedItems.items?.map((artist) => (
          <DraggableArtist key={artist.id} artist={artist} />
        ))}
      </div>
    </div>
  );
};

export default TierList;
