import React, { useState } from 'react';
import { flip } from 'svelte/animate'; // You might need to replace this animation library with a React equivalent
import { colors } from '../colors';
import { TierItem } from '../tier-items/TierItem'; // Assuming TierItem is a React component
import { useDndZone } from 'svelte-dnd-action'; // This would need to be replaced with a React equivalent hook
import { flipDurationMs } from '../lib/constants';

const TierComponent = ({ tier }) => {
  const [items, setItems] = useState([]);

  const setBgColor = (node) => {
    node.style.backgroundColor =
      '#' + colors.find((color) => color.rank === tier.rank).color;
  };

  const onDrag = (e) => {
    try {
      setItems(e.detail.items);
    } catch (error) {
      console.log(error);
    }
  };

  const onDrop = (e) => {
    try {
      setItems(e.detail.items);
    } catch (error) {
      console.log(error);
    }
  };

  const dndZoneProps = useDndZone({ items, flipDurationMs, type: 'tier-items' });

  return (
    <>
      <span
        ref={setBgColor}
        className="w-40 h-full flex flex-col justify-center text-center text-black p-2"
      >
        <input
          onClick={(e) => e.target.select()}
          tabIndex={tier.rank}
          type="text"
          className="focus:border-b-black focus:border-b-2 text-center bg-transparent focus:outline-none font-black text-2xl"
          value={tier.label}
        />
      </span>
      <section
        className="h-full w-full p-4 flex flex-wrap"
        {...dndZoneProps}
        onConsider={onDrag}
        onFinalize={onDrop}
      >
        {items.map((item) => (
          <div key={item.id} className="mx-1 h-full">
            {/* Assuming TierItem is a React component */}
            <TierItem fontSize="xl" item={item} tier={tier} />
          </div>
        ))}
      </section>
    </>
  );
};

export default TierComponent;
