import React, { useState, useEffect } from 'react';
import * as Utils from './Utils';

const FindArtist = ({ onSearch }) => {
    const [popularity, setPopularity] = useState('');
    const [genre, setGenre] = useState('');
    const [searchResult, setSearchResult] = useState([]);
    const [error, setError] = useState(null);
    const [accessToken, setAccessToken] = useState('');

    useEffect(() => {
        const token = localStorage.getItem('access_token');
        if (token) {
            setAccessToken(token);
        }
    }, []);

    // Function to fetch recommended artists
    const fetchRecommendedArtists = async () => {
        try {
            if (!accessToken) {
                throw new Error('Access token not found');
            }
            const recommendedArtists = await Utils.getRecommendedArtists(accessToken, genre, popularity); // Use getRecommendedArtists utility function
            setSearchResult(recommendedArtists.artists || []);
        } catch (error) {
            setError(error.message);
        }
    };

    // Function to fetch 10 random artists matching the criteria
    const fetchRandomArtists = async () => {
        try {
            if (!accessToken) {
                throw new Error('Access token not found');
            }
            const randomArtists = await Utils.getRandomArtists(accessToken, genre, popularity); // Implement getRandomArtists utility function
            setSearchResult(randomArtists || []);
        } catch (error) {
            setError(error.message);
        }
    };

    const handleSearch = async () => {
        if (genre && popularity) {
            fetchRandomArtists(); // Fetch random artists if both genre and popularity are provided
        } else {
            fetchRecommendedArtists(); // Fetch recommended artists if any of genre or popularity is missing
        }
    };

    return (
        <div>
            <h2>Search for Artists</h2>
            <div>
                <label>
                    Popularity:
                    <select value={popularity} onChange={(e) => setPopularity(e.target.value)}>
                        <option value="">Select Popularity</option>
                        <option value="very-popular">Very Popular</option>
                        <option value="popular">Popular</option>
                        <option value="moderate">Moderate</option>
                        <option value="niche">Niche</option>
                        <option value="underground">Underground</option>
                    </select>
                </label>
            </div>
            <div>
                <label>
                    Genre:
                    <input type="text" value={genre} onChange={(e) => setGenre(e.target.value)} />
                </label>
            </div>
            <button onClick={handleSearch}>Search</button>
            {error && <p>Error: {error}</p>}
            {searchResult && searchResult.length > 0 && (
                <div>
                    <h3>Search Result</h3>
                    <ul>
                        {searchResult.map(artist => (
                            <li key={artist.id}>{artist.name}</li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default FindArtist;
