import React, { useState, useEffect } from 'react';
import * as Utils from './Utils';

function Home() {
    const [city, setCity] = useState('');
    const [weatherData, setWeatherData] = useState(null);
    const [errorMessage, setErrorMessage] = useState('');
    const [userName, setUserName] = useState('');
    const [loggedIn, setLoggedIn] = useState(false);
    const [theme, setTheme] = useState(() => localStorage.getItem('theme') || 'main');

    const handleLogin = Utils.authenticate;
    const handleLogout = () => {
        localStorage.removeItem('access_token');
        localStorage.removeItem('refresh_token');
        setLoggedIn(false);
        window.location.href = '/';
    };

    useEffect(() => {
        Utils.handleAuthorizationCode()
            .then(() => {
                const accessToken = localStorage.getItem('access_token');
                if (accessToken) {
                    console.log('Access Token:', accessToken); // Logging access token
                    Utils.getProfile(accessToken)
                        .then(data => {
                            if (data) {
                                console.log('User Profile:', data); // Logging user profile
                                setUserName(data.display_name);
                            }
                        })
                        .catch(error => {
                            console.error('Error fetching user profile:', error);
                        });
                } else {
                    console.error('Access token not found');
                }
            })
            .catch(error => {
                console.error('Error handling authorization code:', error);
            });
    }, []);

    useEffect(() => {
        const lastCity = localStorage.getItem('lastCity');
        if (lastCity) {
            setCity(lastCity);
            fetchWeather(lastCity);
        } else {
            Utils.getUserLocation()
                .then(location => {
                    fetchWeather(location.latitude, location.longitude);
                })
                .catch(error => {
                    console.error('Error getting user location:', error);
                });
        }

        const accessToken = localStorage.getItem('access_token');
        if (accessToken) {
            Utils.getProfile(accessToken)
                .then(data => {
                    if (data) {
                        setUserName(data.display_name);
                    }
                })
                .catch(error => {
                    console.error('Error fetching user profile:', error);
                });
        } else {
            console.error('Access token not found');
        }
    }, []);

    useEffect(() => {
        const accessToken = localStorage.getItem('access_token');
        const refreshToken = localStorage.getItem('refresh_token');

        if (accessToken) {
            Utils.getProfile(accessToken, refreshToken)
                .then(data => {
                    if (data) {
                        setUserName(data.display_name);
                        setLoggedIn(true);
                    }
                })
                .catch(error => {
                    console.error('Error fetching user profile:', error);
                });
        } else {
            console.error('Access token not found');
        }
    }, []);

    useEffect(() => {
        document.body.classList.toggle('light-theme', theme === 'light');
        localStorage.setItem('theme', theme);
    }, [theme]);

    const handleSearch = () => {
        fetchWeather(city);
    };

    const handleChange = e => {
        setCity(e.target.value);
    };

    const handleKeyDown = e => {
        if (e.key === 'Enter') {
            handleSearch();
        }
    };

    const toggleTheme = () => {
        setTheme(prevTheme => (prevTheme === 'main' ? 'light' : 'main'));
    };

    const fetchWeather = (latitude, longitude) => {
        Utils.fetchWeatherData(latitude, longitude)
            .then(({ data, error }) => {
                if (error) {
                    setErrorMessage('Niepoprawna nazwa miasta');
                    setWeatherData(null);
                } else {
                    setWeatherData(data);
                    setErrorMessage('');
                }
            })
            .catch(error => {
                console.error('Error fetching weather data:', error);
            });
    };

    return (
        <>
            <div className="container">
                <div className="card">
                    <div className="search">
                        <input
                            type="text"
                            placeholder="Enter city name"
                            spellCheck="false"
                            value={city}
                            onChange={handleChange}
                            onKeyDown={handleKeyDown}
                        />
                        <button onClick={handleSearch}>Search</button>
                    </div>
                    {errorMessage && <div className="error"><p>{errorMessage}</p></div>}
                    {weatherData && !errorMessage && (
                        <div className="weather">
                            <h3>{userName ? `Cześć, ${userName}!` : 'Niepoprawna nazwa miasta'}</h3>
                            <h2>Weather in {weatherData.name}</h2>
                            <p>Temperature: {Math.round(weatherData.main.temp)}°C</p>
                            <p>Humidity: {weatherData.main.humidity}%</p>
                            <p>Wind Speed: {weatherData.wind.speed} km/h</p>
                        </div>
                    )}
                </div>
                <button className="theme-btn" onClick={toggleTheme}>Theme</button>
                {loggedIn ? (
                    <button onClick={handleLogout}>Logout</button>
                ) : (
                    <button onClick={handleLogin}>Login with Spotify</button>
                )}
            </div>
        </>
    );
}

export default Home;
