// src/TierList.js
import React, { useState } from 'react';
import Tier from './Tier';
import '../styles/TierList.css';

const TierList = () => {
  const tiers = ['S', 'A', 'B', 'C', 'D'];

  const [items, setItems] = useState({
    item1: null,
    item2: null,
    item3: null,
    item4: null,
  });

  const handleDrop = (itemId, tier) => {
    setItems((prevItems) => ({
      ...prevItems,
      [itemId]: tier,
    }));
  };

  return (
    <div className="tier-list-container">
      <h1>Tier List</h1>
      <div className="tier-list">
        {tiers.map((tier) => (
          <Tier key={tier} tier={tier} items={items} onDrop={handleDrop} />
        ))}
      </div>

      <div className="draggable-items">
        <div className="item" draggable="true" id="item1">
          Item 1
        </div>
        <div className="item" draggable="true" id="item2">
          Item 2
        </div>
        <div className="item" draggable="true" id="item3">
          Item 3
        </div>
        <div className="item" draggable="true" id="item4">
          Item 4
        </div>
      </div>
    </div>
  );
};

export default TierList;
