import React, { useState } from 'react';

const FindMusic = () => {
  const [selectedGenres, setSelectedGenres] = useState([]);
  const [tempo, setTempo] = useState(0); // BPM
  const [acousticness, setAcousticness] = useState(0);
  const [danceability, setDanceability] = useState(0);
  const [popularity, setPopularity] = useState('Niche');

  // Function to handle changes in selected genres
  const handleGenreChange = (event) => {
    const { value } = event.target;
    // Implement logic to update selected genres state
  };

  // Function to handle changes in tempo
  const handleTempoChange = (event) => {
    const { value } = event.target;
    // Implement logic to update tempo state
  };

  // Function to handle changes in acousticness
  const handleAcousticnessChange = (event) => {
    const { value } = event.target;
    // Implement logic to update acousticness state
  };

  // Function to handle changes in danceability
  const handleDanceabilityChange = (event) => {
    const { value } = event.target;
    // Implement logic to update danceability state
  };

  // Function to handle changes in popularity
  const handlePopularityChange = (event) => {
    const { value } = event.target;
    // Implement logic to update popularity state
  };

  // Function to handle form submission
  const handleSubmit = (event) => {
    event.preventDefault();
    // Implement logic to fetch recommended songs based on selected options
  };

  return (
    <>
    <h1>Find Music</h1>
    <div>
      <form onSubmit={handleSubmit}>
        {/* Select genres */}
        <label>Select Genre:</label>
        <select multiple value={selectedGenres} onChange={handleGenreChange}>
          {/* Options for genres */}
        </select>

        {/* Input for tempo */}
        <label>Tempo (BPM):</label>
        <input type="number" value={tempo} onChange={handleTempoChange} />

        {/* Input for acousticness */}
        <label>Acousticness:</label>
        <input type="number" step="0.01" value={acousticness} onChange={handleAcousticnessChange} />

        {/* Input for danceability */}
        <label>Danceability:</label>
        <input type="number" step="0.01" value={danceability} onChange={handleDanceabilityChange} />

        {/* Select popularity */}
        <label>Popularity:</label>
        <select value={popularity} onChange={handlePopularityChange}>
          <option value="Niche">Niche</option>
          <option value="Underground">Underground</option>
          <option value="Popular">Popular</option>
          <option value="Very Popular">Very Popular</option>
        </select>

        {/* Submit button */}
        <button type="submit">Recommend Songs</button>
      </form>
    </div>
    </>
  );
};

export default FindMusic;
