import React, { useState } from 'react';
import * as Utils from './Utils'; // Import your Utils.js file

const FindArtist = ({ onSearch }) => {
    const [popularity, setPopularity] = useState('');
    const [genre, setGenre] = useState('');
    const [searchResult, setSearchResult] = useState(null);
    const [error, setError] = useState(null);

    const handleSearch = async () => {
        try {
            // Construct the query based on user selections
            const query = {
                // Include popularity and genre in the query as needed
                // Example: popularity, genre,
            };
            // Fetch artists based on the query
            const artists = await Utils.getArtists(['2CIMQHirSU0MQqyYHq0eOx', '57dN52uHvrHOxijzpIgu3E', '1vCWHaC5f2uS3yhpwWbIA6'], query);
            setSearchResult(artists);
        } catch (error) {
            setError(error.message);
        }
    };

    return (
        <div>
            <h2>Search for Artists</h2>
            {/* Add UI for selecting popularity and genre */}
            <button onClick={handleSearch}>Search</button>
            {error && <p>Error: {error}</p>}
            {searchResult && (
                <div>
                    <h3>Search Result</h3>
                    <ul>
                        {searchResult.map(artist => (
                            <li key={artist.id}>{artist.name}</li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default FindArtist;
