import React, { useState, useEffect } from 'react';
import { openWeatherApiKey, spotifyClientId, spotifyClientSecret, spotifyRedirectUri } from './ApiKeys';

async function authenticate() {
    const clientId = spotifyClientId;
    const redirectUri = spotifyRedirectUri;
    const scopes = ['user-read-private', 'user-read-email', 'user-top-read'];

    window.location.href = `https://accounts.spotify.com/authorize?client_id=${clientId}&redirect_uri=${redirectUri}&scope=${scopes.join('%20')}&response_type=code`;
}

async function handleAuthorizationCode() {
    const params = new URLSearchParams(window.location.search);
    const authorizationCode = params.get('code');

    if (authorizationCode) {
        try {
            const response = await fetch('https://accounts.spotify.com/api/token', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded',
                    Authorization: `Basic ${btoa(`${spotifyClientId}:${spotifyClientSecret}`)}`
                },
                body: `grant_type=authorization_code&code=${authorizationCode}&redirect_uri=${spotifyRedirectUri}`
            });

            if (!response.ok) {
                throw new Error(`Failed to exchange authorization code for access token. Status: ${response.status}`);
            }

            const data = await response.json();
            const accessToken = data.access_token;

            if (!accessToken) {
                throw new Error('Access token not found in response');
            }

            localStorage.setItem('access_token', accessToken);
            window.location.href = '/';
        } catch (error) {
            console.error('Error exchanging authorization code:', error);
        }
    }
}

async function getProfile(accessToken, refreshToken) {
    try {
        const response = await fetch('https://api.spotify.com/v1/me', {
            headers: {
                Authorization: 'Bearer ' + accessToken
            }
        });

        const data = await response.json();
        return data;
    } catch (error) {
        if (error.message === "Unauthorized") {
            try {
                const newAccessToken = await refreshAccessToken(refreshToken);
                localStorage.setItem('access_token', newAccessToken);
                return getProfile(newAccessToken, refreshToken);
            } catch (refreshError) {
                console.error('Error refreshing access token:', refreshError);
                throw refreshError;
            }
        } else {
            throw error;
        }
    }
}

async function refreshAccessToken(refreshToken) {
    const clientCredentials = btoa(`${spotifyClientId}:${spotifyClientSecret}`);
    const spotifyTokenResponse = await fetch("https://accounts.spotify.com/api/token", {
        method: "POST",
        headers: {
            "Content-Type": "application/x-www-form-urlencoded",
            Authorization: `Basic ${clientCredentials}`,
        },
        body: `grant_type=refresh_token&refresh_token=${refreshToken}`,
        cache: "no-cache"
    });

    const spotifyTokenData = await spotifyTokenResponse.json();
    return spotifyTokenData.access_token;
}

async function getTopTracks(accessToken) {
    try {
        const response = await fetch('https://api.spotify.com/v1/me/top/tracks?limit=5', {
            headers: {
                Authorization: 'Bearer ' + accessToken
            }
        });

        const data = await response.json();
        return data.items;
    } catch (error) {
        console.error('Error fetching top tracks:', error);
        throw error;
    }
}

function Home() {
    const [city, setCity] = useState('');
    const [weatherData, setWeatherData] = useState(null);
    const [errorMessage, setErrorMessage] = useState('');
    const [userName, setUserName] = useState('');
    const [loggedIn, setLoggedIn] = useState(false);
    const [theme, setTheme] = useState(() => localStorage.getItem('theme') || 'main');
    const [topTracks, setTopTracks] = useState([]);
    const [topArtists, setTopArtists] = useState([]); // Add this line to declare topArtists state

    useEffect(() => {
        handleAuthorizationCode();
    }, []);

    useEffect(() => {
        const lastCity = localStorage.getItem('lastCity');
        if (lastCity) {
            setCity(lastCity);
            fetchWeatherData(lastCity);
        }

        const accessToken = localStorage.getItem('access_token');
        if (accessToken) {
            getProfile(accessToken)
                .then(data => {
                    if (data) {
                        setUserName(data.display_name);
                    }
                })
                .catch(error => {
                    console.error('Error fetching user profile:', error);
                });
        } else {
            console.error('Access token not found');
        }
    }, []);

    useEffect(() => {
        const accessToken = localStorage.getItem('access_token');
        const refreshToken = localStorage.getItem('refresh_token');

        if (accessToken) {
            getProfile(accessToken, refreshToken)
                .then(data => {
                    if (data) {
                        setUserName(data.display_name);
                        setLoggedIn(true);
                    }
                })
                .catch(error => {
                    console.error('Error fetching user profile:', error);
                });
        } else {
            console.error('Access token not found');
        }
    }, []);

    useEffect(() => {
        document.body.classList.toggle('light-theme', theme === 'light');
        localStorage.setItem('theme', theme);
    }, [theme]);

    const fetchWeatherData = async (city) => {
        try {
            const response = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${openWeatherApiKey}`);
            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }
            const data = await response.json();
            setWeatherData(data);
            setErrorMessage('');
            localStorage.setItem('lastCity', city);
        } catch (error) {
            console.error(error);
            setWeatherData(null);
            setErrorMessage('Error fetching weather data. Please try again later.');
        }
    };

    const handleSearch = () => {
        fetchWeatherData(city);
    };

    const handleChange = (e) => {
        setCity(e.target.value);
    };

    const handleKeyDown = (e) => {
        if (e.key === 'Enter') {
            handleSearch();
        }
    };

    const toggleTheme = () => {
        setTheme(prevTheme => (prevTheme === 'main' ? 'light' : 'main'));
    };

    const handleLogout = () => {
        // Clear access token and refresh token from local storage
        localStorage.removeItem('access_token');
        localStorage.removeItem('refresh_token');
        // Redirect to home page or any other desired page
        window.location.href = '/';
    };

    async function handleLogin() {
        window.location.href = 'https://accounts.spotify.com/authorize' +
            '?response_type=code' +
            '&client_id=' + spotifyClientId +
            '&redirect_uri=' + spotifyRedirectUri +
            '&scope=user-read-private%20user-read-email%20user-top-read'; // Include user-top-read scope
    };


    // Sekcja typu <body>
    return (
        <>
            <div className="container">
                <div className="card">
                    <div className="search">
                        <input 
                            type="text" 
                            placeholder="Wprowadź nazwę miasta" 
                            spellCheck="false" 
                            value={city} 
                            onChange={handleChange} 
                            onKeyDown={handleKeyDown} 
                        />
                        <button onClick={handleSearch}>Search</button>
                    </div>
                    {errorMessage && <div className="error"><p>{errorMessage}</p></div>}
                    {weatherData && (
                        <div className="weather">
                            <h3>Cześć, {userName}!</h3> {/* Display user's name here */}
                            <h2>Pogoda w {weatherData.name}</h2>
                            <p>Temperatura: {Math.round(weatherData.main.temp)}°C</p>
                            <p>Wilgotność: {weatherData.main.humidity}%</p>
                            <p>Prędkość wiatru: {weatherData.wind.speed} km/h</p>
                        </div>
                    )}
                    {topArtists && (
                        <div className="top-artists">
                            <h2>Top 5 Artists</h2>
                            <ul>
                                {topArtists.map((artist, index) => (
                                    <li key={index}>{artist.name}</li>
                                ))}
                            </ul>
                        </div>
                    )}
                </div>

                {loggedIn ? (
                    <button onClick={handleLogout}>Logout</button>
                ) : (
                    <button onClick={handleLogin}>Login with Spotify</button>
                )}
            </div>
        </>
    );
}

export default Home;
