// src/Tier.js
import React from 'react';
import Item from './Item';

const Tier = ({ tier, items, onDrop }) => {
  const handleDrop = (e) => {
    const itemId = e.dataTransfer.getData('text/plain');
    onDrop(itemId, tier);
  };

  const allowDrop = (e) => {
    e.preventDefault();
  };

  return (
    <div className="tier" onDrop={handleDrop} onDragOver={allowDrop}>
      <h2>{tier}</h2>
      <div className="items">
        {Object.keys(items).map((itemId) => {
          const itemTier = items[itemId];
          if (itemTier === tier) {
            return <Item key={itemId} itemId={itemId} />;
          }
          return null;
        })}
      </div>
    </div>
  );
};

export default Tier;
