import React, { useState } from 'react';
import * as Utils from './Utils';

const FindArtists = () => {
    const [popularity, setPopularity] = useState('');
    const [genre, setGenre] = useState('');
    const [searchResult, setSearchResult] = useState([]);
    const [error, setError] = useState(null);

    const handleSearch = async () => {
        try {
            const accessToken = localStorage.getItem('access_token');
            const seedGenres = ['pop']; // Specify the genre(s) you want to use as seeds

            // Ensure seedGenres is initialized as an array
            if (!Array.isArray(seedGenres)) {
                throw new Error('seedGenres must be an array');
            }

            // Fetch recommended artists based on popularity and genre
            const response = await Utils.getRecommendedArtists(accessToken, seedGenres, popularity);
            console.log('Recommended Artists:', response); // Log recommended artists
            setSearchResult(response.tracks); // Update search results
        } catch (error) {
            console.error('Error fetching recommended artists:', error);
            setError(error.message); // Set error message
        }
    };

    return (
        <div>
            <h2>Search for Artists</h2>
            <div>
                <label>
                    Popularity:
                    <select value={popularity} onChange={(e) => setPopularity(e.target.value)}>
                        <option value="">Select Popularity</option>
                        <option value="very-popular">Very Popular</option>
                        <option value="popular">Popular</option>
                        <option value="moderate">Moderate</option>
                        <option value="niche">Niche</option>
                        <option value="underground">Underground</option>
                    </select>
                </label>
            </div>
            <div>
                <label>
                    Genre:
                    <input type="text" value={genre} onChange={(e) => setGenre(e.target.value)} />
                </label>
            </div>
            <button onClick={handleSearch}>Search</button>
            {error && <p>Error: {error}</p>}
            {searchResult && searchResult.length > 0 && (
                <div>
                    <h3>Search Result</h3>
                    <ul>
                        {searchResult.map(artist => (
                            <li key={artist.id}>{artist.name}</li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default FindArtists;
