import React, { useState } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import Header from './components/Header';
import Home from './components/Home';
import Profile from './components/Profile';
import FindMusic from './components/FindMusic';
import TierListPage from './components/TierListPage';
import 'bootstrap/dist/css/bootstrap.min.css';
import './styles/styles.css';
import './App.css';
import { handleLogout } from './components/Utils'; // Import handleLogout from Utils.js

function App() {
    const [theme, setTheme] = useState(() => localStorage.getItem('theme') || 'main');
    const [isLoggedIn, setIsLoggedIn] = useState(false); // State to track login status

    const toggleTheme = () => {
        const newTheme = theme === 'main' ? 'light' : 'main';
        setTheme(newTheme);
        localStorage.setItem('theme', newTheme);
        applyTheme(newTheme);
    };

    const applyTheme = (theme) => {
        document.body.classList.toggle('light-theme', theme === 'light');
    };

    const handleLogin = () => {
        // Logic to handle login, set isLoggedIn to true
        setIsLoggedIn(true);
    };

    const handleLogout = () => {
        // Logic to handle logout, set isLoggedIn to false
        setIsLoggedIn(false);
        // Call handleLogout function if needed
    };

    return (
        <div className="App">
            <Header
                isLoggedIn={isLoggedIn}
                toggleTheme={toggleTheme}
                handleLogin={handleLogin}
                handleLogout={handleLogout}
            /> {/* Pass isLoggedIn, handleLogin, and handleLogout as props */}
            <Routes>
                <Route path="/" element={<Home toggleTheme={toggleTheme} />} />
                <Route path="FindMusic" element={<FindMusic toggleTheme={toggleTheme} />} />
                {isLoggedIn ? (
                    <Route path="TierListPage" element={<TierListPage toggleTheme={toggleTheme} />} />
                ) : (
                    <Navigate to="/" /> // Redirect to home if not logged in
                )}
                <Route path="Profile" element={<Profile toggleTheme={toggleTheme} />} />
            </Routes>
        </div>
    );
}

export default App;
