import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import * as Utils from './Utils'; // Use only one import method for cleanliness

export default function Header({ toggleTheme, setIsPlaying }) {
    const [currentPreview, setCurrentPreview] = useState(null);

    const handleHeaderItemClick = () => {
        if (currentPreview) {
            Utils.pausePreview(currentPreview); // Assuming this correctly handles the pause
            setIsPlaying(false); // Correctly updates playing state
        }
    };

    const handleLogoutClick = () => {
        Utils.handleLogout();
        if (currentPreview) {
            Utils.pausePreview(currentPreview); // Pause music on logout as well
            setIsPlaying(false);
        }
    };

    return (
        <>
            <h1 className="is-size-1">Spot The Weather</h1>
            <header>
                <nav className="nawigacja">
                    <button className="theme-btn" onClick={toggleTheme}>Zmień Motyw</button>
                    <ul className="nav justify-content-center">
                        <li className="nav-item">
                            <Link to="/" onClick={handleHeaderItemClick}>Strona Główna</Link>
                        </li>
                        <li className="nav-item">
                            <Link to="FindMusic" onClick={handleHeaderItemClick}>Wyszukiwarka Muzyczna</Link>
                        </li>
                        <li className="nav-item">
                            <Link to="TierListPage" onClick={handleHeaderItemClick}>Tier List</Link>
                        </li>
                        <li className="nav-item">
                            <Link to="Profile" onClick={handleHeaderItemClick}>Profil</Link>
                        </li>
                        <li className="nav-item">
                            {Utils.loggedin() ? (
                                <button className="login-btn" onClick={handleLogoutClick}>Wyloguj</button>
                            ) : (
                                ""
                            )}
                        </li>
                    </ul>
                </nav>
            </header>
        </>
    );
}
