// src/TierList.js
import React, { useState } from 'react';
import Tier from './Tier';

const TierList = () => {
  const tiers = ['S', 'A', 'B', 'C']; // Define your tier labels

  // State to hold tier assignments for items
  const [items, setItems] = useState({
    item1: null,
    item2: null,
    item3: null,
    item4: null,
  });

  const handleDrop = (item, tier) => {
    setItems({ ...items, [item]: tier });
  };

  return (
    <div className="tier-list">
      {tiers.map((tier) => (
        <Tier key={tier} tier={tier} items={items} onDrop={handleDrop} />
      ))}
    </div>
  );
};

export default TierList;
