import React from 'react';
import { Link } from 'react-router-dom';
import { loggedin, handleLogout } from './Utils'; // Import loggedIn and handleLogout functions

export default function Header({ toggleTheme }) {
    return (
        <>
            <h1 className="is-size-1">Spot The Weather</h1>
            <header>
                <nav className="nawigacja">
                    <div> {/* Wrapping theme button in a div */}
                        <button className="theme-btn" onClick={toggleTheme}>Zmień Motyw</button>
                    </div>
                    <div> {/* Wrapping logout button in a div */}
                        <ul className="nav justify-content-center">
                            <li className="nav-item">
                                <Link to="/">Strona Główna</Link>
                            </li>
                            <li className="nav-item">
                                <Link to="FindMusic">Wyszukiwarka Muzyczna</Link>
                            </li>
                            <li className="nav-item">
                                <Link to="TierListPage">Tier List</Link>
                            </li>
                            <li className="nav-item">
                                <Link to="Profile">Profil</Link>
                            </li>
                            <li className="nav-item">
                                {loggedin() ? ( // Check if user is logged in
                                    <button className="theme-btn" onClick={() => handleLogout()}>Wyloguj się</button>
                                ) : (
                                    <Link to="/login">Login</Link> // Render a login link if user is not logged in
                                )}
                            </li>
                        </ul>
                    </div>
                </nav>
            </header>
        </>
    );
}
