import React, { useState } from 'react';
import { searchMusicRecommendations } from './Utils';

const FindMusic = () => {
  // State declarations
  const [selectedGenre, setSelectedGenre] = useState('');
  const [tempo, setTempo] = useState('');
  const [popularity, setPopularity] = useState({ min: 0, max: 100, target: 50 });
  const [limit, setLimit] = useState(10); // Initialize limit state
  const [recommendedTracks, setRecommendedTracks] = useState([]);

  // Event handler functions
  const handleGenreChange = (event) => {
    setSelectedGenre(event.target.value);
  };

  const handleTempoChange = (event) => {
    setTempo(event.target.value);
  };

  const handlePopularityChange = (event) => {
    const selectedPopularity = event.target.value;
    let minPopularity, maxPopularity;

    switch (selectedPopularity) {
      case 'Niche':
        minPopularity = 0;
        maxPopularity = 25;
        break;
      case 'Underground':
        minPopularity = 26;
        maxPopularity = 50;
        break;
      case 'Popular':
        minPopularity = 51;
        maxPopularity = 75;
        break;
      case 'VeryPopular':
        minPopularity = 76;
        maxPopularity = 100;
        break;
      default:
        minPopularity = 0;
        maxPopularity = 100;
    }

    setPopularity({ min: minPopularity, max: maxPopularity });
  };


  const handleLimitChange = (event) => {
    setLimit(parseInt(event.target.value)); // Parse the value to integer and update the limit state
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const accessToken = localStorage.getItem('access_token');
      const response = await searchMusicRecommendations(
        accessToken,
        selectedGenre,
        tempo,
        popularity,
        limit // Pass the limit as an argument to the searchMusicRecommendations function
      );
      setRecommendedTracks(response.tracks);
    } catch (error) {
      console.error('Error fetching recommended tracks:', error);
    }
  };

  return (
    <>
      <h1>Find Music</h1>
      <div>
        <form onSubmit={handleSubmit}>
          <label>Select Genre:</label>
          <select value={selectedGenre} onChange={handleGenreChange}>
            <option value="">Select Genre</option>
            <option value="pop">Pop</option>
            <option value="hip-hop">Rap/Hip-Hop</option>
            <option value="rock">Rock/Metal</option>
            <option value="indie">Indie</option>
            <option value="jazz">Jazz</option>
            <option value="classical">Classical</option>
            <option value="electronic">Electronic</option>
            <option value="disco">Disco</option>
            <option value="new-age">New Age</option>
            <option value="folk">Folk</option>
          </select>

          <label>Tempo:</label>
          <select value={tempo} onChange={handleTempoChange}>
            <option value="">Select BPM</option>
            <option value="fast">Fast</option>
            <option value="slow">Slow</option>
            <option value="calm">Calm</option>
          </select>

          <label>Popularity:</label>
          <select value={popularity} onChange={handlePopularityChange}>
            <option value="Niche">Niche</option>
            <option value="Underground">Underground</option>
            <option value="Popular">Popular</option>
            <option value="VeryPopular">Very Popular</option>
          </select>

          <label>Number of Songs:</label>
          <select value={limit} onChange={handleLimitChange}>
            <option value="10">10</option>
            <option value="20">20</option>
            <option value="30">30</option>
          </select>

          <button type="submit">Recommend Songs</button>
        </form>
      </div>

      <div>
        <h3>Recommended Songs</h3>
        <ul>
          {recommendedTracks
            .map((track, index) => (
                <li key={index}>
                  {track.name} - {track.artists.map((artist) => artist.name).join(', ')}
                </li>
              ))}
          </ul>
        </div>
      </>
    );
  };

  export default FindMusic;
