import React, { useState, useEffect } from 'react';
import * as Utils from './Utils';

const FindMusic = () => {
  const [selectedGenres, setSelectedGenres] = useState([]);
  const [tempo, setTempo] = useState(''); // Change tempo state to string type
  const [popularity, setPopularity] = useState('Niche');
  const [recommendedTracks, setRecommendedTracks] = useState([]);

  // Function to handle changes in selected genres
  const handleGenreChange = (event) => {
    const { value } = event.target;
    setSelectedGenres((prevGenres) => [...prevGenres, value]);
  };

  // Function to handle changes in tempo
  const handleTempoChange = (event) => {
    const { value } = event.target;
    setTempo(value);
  };

  // Function to handle changes in popularity
  const handlePopularityChange = (event) => {
    const { value } = event.target;
    setPopularity(value);
  };

  // Function to handle form submission
  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const accessToken = localStorage.getItem('access_token');
      const response = await Utils.getRecommendations(
        accessToken,
        null,
        selectedGenres.join(','),
        null
      );
      setRecommendedTracks(response.tracks);
    } catch (error) {
      console.error('Error fetching recommended tracks:', error);
    }
  };

  return (
    <>
      <h1>Find Music</h1>
      <div>
        <form onSubmit={handleSubmit}>
          {/* Select genres */}
          <label>Select Genre:</label>
          <select multiple onChange={handleGenreChange}>
            {/* Options for genres */}
            <option value="rock">Rock</option>
            <option value="pop">Pop</option>
            {/* Add more options as needed */}
          </select>

          {/* Select tempo (BPM) */}
          <label>Tempo:</label>
          <select value={tempo} onChange={handleTempoChange}>
            <option value="">Select BPM</option>
            <option value="fast">Fast</option> {/* Assign appropriate BPM values */}
            <option value="slow">Slow</option>
            <option value="calm">Calm</option>
          </select>

          {/* Select popularity */}
          <label>Popularity:</label>
          <select value={popularity} onChange={handlePopularityChange}>
            <option value="Niche">Niche</option>
            <option value="Underground">Underground</option>
            <option value="Popular">Popular</option>
            <option value="Very Popular">Very Popular</option>
          </select>

          {/* Submit button */}
          <button type="submit">Recommend Songs</button>
        </form>
      </div>

      {/* Display recommended tracks */}
      <div>
        <h3>Recommended Songs</h3>
        <ul>
          {recommendedTracks.map((track, index) => (
            <li key={index}>
              {track.name} - {track.artists.map((artist) => artist.name).join(', ')}
            </li>
          ))}
        </ul>
      </div>
    </>
  );
};

export default FindMusic;
